import React, { useState, useEffect } from "react";
import { Col, Button, Form } from "react-bootstrap";
import { Link } from "react-router-dom";

function Registration({ addUserHandler, history }) {
  const [email, setEmail] = useState("");
  const [confirmEmail, setConfirmEmail] = useState("");
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const [isDisabled, setIsDisabled] = useState(true);
  const [showSuccess, setShowSuccess] = useState(false);

  const registrationHandler = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:3003/users/register", {
        method: "POST",
        body: JSON.stringify({
          email: email,
          password: password,
          name: name,
        }),
        headers: {
          "Content-Type": "application/json",
        },
      });
      const data = await response.json();
      if (response.ok) {
        setShowSuccess(true);
        setEmail("");
        setConfirmEmail("");
        setName("");
        setPassword("");
        setTimeout(() => {
          history.push("/login");
        }, 1000);
      }
      console.log(data);
    } catch (err) {
      console.log(err);
    }
  };

  const handleInputMail = (e) => {
    setEmail(e.target.value);
  };
  const handleConfirmInputMail = (e) => {
    setConfirmEmail(e.target.value);
  };
  const handleInputName = (e) => {
    setName(e.target.value);
  };
  const handleInputPassword = (e) => {
    setPassword(e.target.value);
  };

  const signUpButtonEnabler = () => {
    if (email.length !== 0 && confirmEmail.length !== 0 && name.length !== 0 && password.length !== 0) {
      if (email === confirmEmail) {
        setIsDisabled(false);
      } else {
        setIsDisabled(true);
      }
    } else {
      setIsDisabled(true);
    }
  };

  useEffect(() => {
    signUpButtonEnabler();
  }, [email, confirmEmail, name, password]);

  return (
    <div className="container_sign_in">
      <Col className="main_sing_in pt-3">
        <div className="text-center">
          <img
            src="data:image/png;base64,"
            alt="spotify logo"
            className="sign_logo"
          />
        </div>
        <span>
          <h3 className="text-center sign_title mb-4">Sing up for free to start listening.</h3>
        </span>
        <button id="sing_in_with_fb" href="#" className="col-12">
          SIGN UP WITH FACEBOOK
        </button>
        <div className="hrs">
          <div className="hr"></div>
          <p className="ml-3 mr-3">or</p>
          <div className="hr"></div>
        </div>
        <div className="text-center mt-3 mb-3">Sing up with your email address</div>
        <Form onSubmit={(event) => registrationHandler(event)}>
          <Form.Group controlId="formBasicEmail">
            <Form.Label>What's your email?</Form.Label>
            <Form.Control
              type="email"
              placeholder="Enter your email."
              value={email}
              onChange={handleInputMail}
              required
            />
          </Form.Group>

          <Form.Group controlId="formBasicEmail">
            <Form.Label>Confirm your email</Form.Label>
            <Form.Control
              type="email"
              placeholder="Enter your email again."
              value={confirmEmail}
              onChange={handleConfirmInputMail}
            />
          </Form.Group>

          <Form.Group controlId="formBasicPassword">
            <Form.Label>Create a password</Form.Label>
            <Form.Control
              type="password"
              placeholder="Create a password."
              value={password}
              onChange={handleInputPassword}
              required
            />
          </Form.Group>

          <Form.Group controlId="formBasicName">
            <Form.Label>What should we call you?</Form.Label>
            <Form.Control
              type="name"
              placeholder="Enter a profile name."
              value={name}
              onChange={handleInputName}
              required
            />
            <h6>This appears on your profile.</h6>
          </Form.Group>

          <Form.Label>What's your date of birth?</Form.Label>
          <Form.Row>
            <Form.Group as={Col} controlId="formGridDay">
              <Form.Label>Day</Form.Label>
              <Form.Control placeholder="DD" />
            </Form.Group>

            <Form.Group as={Col} controlId="formGridMonth">
              <Form.Label>Month</Form.Label>
              <Form.Control as="select" defaultValue="Month">
                <option>Month</option>
                <option>...</option>
              </Form.Control>
            </Form.Group>

            <Form.Group as={Col} controlId="formGridYear">
              <Form.Label>Year</Form.Label>
              <Form.Control placeholder="YYYY" />
            </Form.Group>
          </Form.Row>

          <Form.Label>What's your gender?</Form.Label>
          <div className="my_checkbox">
            <Form.Group controlId="formBasicRadio" className="mr-3">
              <Form.Check inline type="radio" label="Male" id="custom-inline-radio-1" />
            </Form.Group>
            <Form.Group controlId="formBasicRadio" className="mr-3">
              <Form.Check inline type="radio" label="Female" id="custom-inline-radio-1" />
            </Form.Group>
            <Form.Group controlId="formBasicRadio" className="mr-3">
              <Form.Check inline type="radio" label="Non-binary" id="custom-inline-radio-1" />
            </Form.Group>
          </div>
          <Form.Group controlId="formBasicCheckbox" id="formBasicCheckbox">
            <div>
              <Form.Check inline type="checkbox" label="" id="custom-inline-checkbox-1" />
            </div>
            <div>
              Share my registration data with Spotify's content providers for marketing purposes. Note that your data
              may be transferred to a country outside of the EEA as described in our privacy policy.
            </div>
          </Form.Group>

          <small>
            By clicking on Sign up, you agree to Spotify's Terms and Conditions of Use. To learn more about how Spotify
            collects, uses, shares and protects your personal data please read Spotify's <g>Privacy Policy</g>.
          </small>

          <button id="sing_in" href="/login.html" className="col-12 mt-2" type="submit" disabled={isDisabled}>
            SIGN UP
          </button>

          <div className="text-center mb-3">
            Have an account? <Link to="/login">Log in</Link>.
          </div>
        </Form>
      </Col>
    </div>
  );
}

export default Registration;
